jsTest:
it("should declare variables of different types", () => {
  const result = declareVariables();
  assert.strictEqual(result.count, 10);
  assert.strictEqual(result.price, 3.99);
  assert.strictEqual(result.USERNAME, "student");
  assert.strictEqual(result.isActive, true);
});
it("should declare USERNAME as a constant", () => {
  const result = declareVariables();
  assert.throws(() => { result.USERNAME = "teacher"; }, TypeError);
});
