it("should update address without modifying original object", () => {
  const originalAddress = {
    street: "123 Main St",
    city: "Boston",
    zip: "02108"
  };
  const originalCopy = {...originalAddress};

  const updatedAddress = updateAddress(originalAddress, "456 Park Ave", "New York");

  // Check that original wasn't modified
  assert.deepStrictEqual(originalAddress, originalCopy);

  // Check that new object has correct values
  assert.strictEqual(updatedAddress.street, "456 Park Ave");
  assert.strictEqual(updatedAddress.city, "New York");
  assert.strictEqual(updatedAddress.zip, "02108");
});
