it("should calculate total with tax using helper function", () => {
  const items = [
    { name: 'Item 1', price: 10 },
    { name: 'Item 2', price: 20 },
    { name: 'Item 3', price: 30 }
  ];

  assert.strictEqual(calculateTotal(items, 0.1), 66); // (10+20+30) * 1.1 = 66
  assert.strictEqual(calculateTotal(items, 0.2), 72); // (10+20+30) * 1.2 = 72
  assert.strictEqual(calculateTotal([], 0.1), 0);    // Empty array should return 0
});
