def test_calculate_total(self):
    items = [
        {"name": "Item 1", "price": 10},
        {"name": "Item 2", "price": 20},
        {"name": "Item 3", "price": 30}
    ]

    self.assertEqual(calculate_total(items, 0.1), 66)  # (10+20+30) * 1.1 = 66
    self.assertEqual(calculate_total(items, 0.2), 72)  # (10+20+30) * 1.2 = 72
    self.assertEqual(calculate_total([], 0.1), 0)      # Empty list should return 0
