jsTest:
it("should return a countdown array from n to 1", () => {
  assert.deepStrictEqual(countDown(5), [5, 4, 3, 2, 1]);
  assert.deepStrictEqual(countDown(3), [3, 2, 1]);
  assert.deepStrictEqual(countDown(1), [1]);

  // Test for purity (no side effects)
  const result1 = countDown(4);
  const result2 = countDown(4);
  assert.notStrictEqual(result1, result2);
});
