it("should filter out odd numbers and double the even ones", () => {
  assert.deepStrictEqual(filterAndMap([1, 2, 3, 4]), [4, 8]);
  assert.deepStrictEqual(filterAndMap([2, 4, 6]), [4, 8, 12]);
