function miniProject(nums) {
  // TODO:
  // 1. Filter out odd numbers: const evens = nums.filter(n => n % 2 === 0);
  // 2. Square each even number: const squared = evens.map(n => n * n);
  // 3. Build a message: `Processed: ${squared.join(',')}`;
  // 4. Return an object: { input: nums, processed: squared, message }
}

jsTest:
it("should filter, square, and summarize correctly", () => {
  const result = miniProject([1, 2, 3, 4, 5, 6]);
  assert.deepStrictEqual(result.input, [1, 2, 3, 4, 5, 6]);
  assert.deepStrictEqual(result.processed, [4, 16, 36]);
  assert.strictEqual(result.message, "Processed: 4,16,36");
  const empty = miniProject([]);
  assert.deepStrictEqual(empty.input, []);
  assert.deepStrictEqual(empty.processed, []);
  assert.strictEqual(empty.message, "Processed: ");
});
