it("should create an immutable point object", () => {
  const point = createPoint(10, 20);
  assert.strictEqual(point.x, 10);
  assert.strictEqual(point.y, 20);
  try { point.x = 30; } catch(e) {}
  try { point.y = 40; } catch(e) {}
  assert.strictEqual(point.x, 10);
  assert.strictEqual(point.y, 20);
});
