it("should count word frequencies correctly", () => {
  assert.deepStrictEqual(wordFrequency("hello world hello"), {hello: 2, world: 1});
  assert.deepStrictEqual(wordFrequency("the quick brown fox jumps over the lazy dog"), 
    {the: 2, quick: 1, brown: 1, fox: 1, jumps: 1, over: 1, lazy: 1, dog: 1});
  assert.deepStrictEqual(wordFrequency(""), {});
});
