jsTest:
it("should analyze the words array correctly", () => {
  const input = ["apple","banana","apple","cherry","banana"];
  const result = miniProject(input);
  assert.deepStrictEqual(result.input, input);
  assert.deepStrictEqual(result.unique, ["apple","banana","cherry"]);
  assert.deepStrictEqual(result.frequency, { apple: 2, banana: 2, cherry: 1 });
  assert.deepStrictEqual(result.reversed, ["banana","cherry","apple","banana","apple"]);

  const empty = miniProject([]);
  assert.deepStrictEqual(empty.input, []);
  assert.deepStrictEqual(empty.unique, []);
  assert.deepStrictEqual(empty.frequency, {});
  assert.deepStrictEqual(empty.reversed, []);
});
