it("should produce a correct summary of algorithms applied to the array", () => {
  const arr = [3, 1, 4, 2];
  const result = miniProject(arr, 4);
  assert.deepStrictEqual(result.input, [3, 1, 4, 2]);
  assert.strictEqual(result.index, 2);
  assert.deepStrictEqual(result.sorted, [1, 2, 3, 4]);
  assert.strictEqual(result.max, 4);
  assert.strictEqual(result.factorialOfMax, 24);
  assert.deepStrictEqual(result.pairIndices, [0, 1]);
});
