jsTest:
it("should reverse an array without using built-in methods", () => {
  const original = [1, 2, 3, 4, 5];
  const result = reverseArray(original);
  assert.deepStrictEqual(result, [5, 4, 3, 2, 1]);
  assert.deepStrictEqual(original, [1, 2, 3, 4, 5]);
  assert.deepStrictEqual(reverseArray([]), []);
  assert.deepStrictEqual(reverseArray([1]), [1]);
});
