exercises.jsTemplate:
function convertTypes(stringNum, numberValue, booleanValue) {
  // TODO: Return an object with the following properties:
  // - stringAsNumber: Convert stringNum to a number
  // - numberAsString: Convert numberValue to a string
  // - booleanAsString: Convert booleanValue to a string
  // - numberAsBoolean: Convert numberValue to a boolean

}

exercises.jsTest:
it("should correctly convert between different types", () => {
  const result = convertTypes("42", 7, true);
  assert.strictEqual(result.stringAsNumber, 42);
  assert.strictEqual(result.numberAsString, "7");
  assert.strictEqual(result.booleanAsString, "true");
  assert.strictEqual(result.numberAsBoolean, true);

  const result2 = convertTypes("0", 0, false);
  assert.strictEqual(result2.stringAsNumber, 0);
  assert.strictEqual(result2.numberAsString, "0");
  assert.strictEqual(result2.booleanAsString, "false");
  assert.strictEqual(result2.numberAsBoolean, false);
});

