exercises.jsTemplate:
function calculateRectangleProperties(length, width) {
  // TODO: Return an object with:
  // - area: the area of the rectangle (length × width)
  // - perimeter: the perimeter of the rectangle (2 × (length + width))
  // - isSquare: boolean indicating if the rectangle is a square (length === width)

}

exercises.jsTest:
it("should calculate rectangle properties correctly", () => {
  const square = calculateRectangleProperties(5, 5);
  assert.strictEqual(square.area, 25);
  assert.strictEqual(square.perimeter, 20);
  assert.strictEqual(square.isSquare, true);

  const rectangle = calculateRectangleProperties(4, 6);
  assert.strictEqual(rectangle.area, 24);
  assert.strictEqual(rectangle.perimeter, 20);
  assert.strictEqual(rectangle.isSquare, false);
});

