  assert.strictEqual(defaultCounter.decrement(), -1);
});

exercises.jsTemplate:
function mergeAndFilter(obj1, obj2, testFn) {
  // TODO: Create a new object that merges properties from obj1 and obj2
  // Include only properties where testFn(value) returns true
  // If both objects have the same property, obj2's value takes precedence

}

exercises.jsTest:
it("should merge objects and filter properties based on test function", () => {
  const obj1 = { a: 1, b: -2, c: 3 };
  const obj2 = { b: 2, d: 4, e: -5 };

  // Test function that keeps only positive numbers
  const keepPositive = (value) => value > 0;

  const result = mergeAndFilter(obj1, obj2, keepPositive);

  // Should have a, b, c, d but not e (as it's negative)
  assert.deepStrictEqual(result, { a: 1, b: 2, c: 3, d: 4 });

  // Test with a different filter (keep even numbers)
  const keepEven = (value) => value % 2 === 0;
  const result2 = mergeAndFilter(obj1, obj2, keepEven);
  assert.deepStrictEqual(result2, { b: 2, d: 4 });
});
