exercises.jsTemplate:
function fibonacci(n) {
  if (n <= 1) return n;
  return fibonacci(n - 1) + fibonacci(n - 2);
}

function memoize(fn) {
  // TODO: Return a wrapper that caches fn results in an object
}

exercises.jsTest:
it("should memoize function results", () => {
  const memoized_fib = memoize(fibonacci);
  assert.strictEqual(memoized_fib(10), 55);

  let count = 0;
  function test_fn(x) { count++; return x * 2; }
  const memoized_test = memoize(test_fn);
  assert.strictEqual(memoized_test(1), 2);
  assert.strictEqual(memoized_test(2), 4);
  assert.strictEqual(count, 2);
  assert.strictEqual(memoized_test(1), 2);
  assert.strictEqual(memoized_test(2), 4);
  assert.strictEqual(count, 2);
});
