  // TODO: Group objects by the specified property
  // Return an object with property values as keys and arrays of objects as values

}

exercises.jsTest:
it("should group objects by property", () => {
  const people = [
    { name: "Alice", age: 30 },
    { name: "Bob", age: 25 },
    { name: "Charlie", age: 30 },
    { name: "Dave", age: 25 }
  ];

  const groupedByAge = groupBy(people, "age");
  assert.strictEqual(Object.keys(groupedByAge).length, 2);
  assert.strictEqual(groupedByAge[25].length, 2);
  assert.strictEqual(groupedByAge[30].length, 2);
  assert.strictEqual(groupedByAge[25][0].name, "Bob");
  assert.strictEqual(groupedByAge[25][1].name, "Dave");
  assert.strictEqual(groupedByAge[30][0].name, "Alice");
  assert.strictEqual(groupedByAge[30][1].name, "Charlie");

  assert.deepStrictEqual(groupBy([], "any"), {});
});

