function deepMerge(obj1, obj2) {
  // TODO: Merge obj2 into obj1, handling nested objects
  // Return a new object without modifying the inputs
  // If both objects have the same key, obj2's value should take precedence

}

exercises.jsTest:
it("should deeply merge two objects", () => {
  const obj1 = { a: 1, b: { c: 2, d: 3 } };
  const obj2 = { b: { c: 4, e: 5 }, f: 6 };
  const expected = { a: 1, b: { c: 4, d: 3, e: 5 }, f: 6 };
  assert.deepStrictEqual(deepMerge(obj1, obj2), expected);

  // Check that originals weren't modified
  assert.deepStrictEqual(obj1, { a: 1, b: { c: 2, d: 3 } });
  assert.deepStrictEqual(obj2, { b: { c: 4, e: 5 }, f: 6 });

  // Simple case
  assert.deepStrictEqual(deepMerge({ a: 1 }, { b: 2 }), { a: 1, b: 2 });
});

