function filterObject(obj, predicate) {
  // TODO: Return a new object with key-value pairs that satisfy the predicate function
  // The predicate function takes (key, value) and returns true to keep the pair or false to exclude it

}

exercises.jsTest:
it("should filter object key-value pairs", () => {
  const obj = { a: 1, b: 2, c: 3, d: 4, e: 5 };

  // Keep only even values
  const evenValues = filterObject(obj, (key, value) => value % 2 === 0);
  assert.deepStrictEqual(evenValues, { b: 2, d: 4 });

  // Keep only keys in first half of alphabet
  const firstHalfKeys = filterObject(obj, (key) => key < 'c');
  assert.deepStrictEqual(firstHalfKeys, { a: 1, b: 2 });

  // Empty object case
  assert.deepStrictEqual(filterObject({}, () => true), {});
});
