exercises.jsTemplate:
function reverseArray(arr) {
  // TODO: Return a new array with elements in reverse order
  // Do not modify the original array
  // Don't use built-in methods like reverse()

}

exercises.jsTest:
it("should reverse an array without using built-in methods", () => {
  const original = [1, 2, 3, 4, 5];
  const result = reverseArray(original);

  assert.deepStrictEqual(result, [5, 4, 3, 2, 1]);
  assert.deepStrictEqual(original, [1, 2, 3, 4, 5]); // Original should be unchanged

  assert.deepStrictEqual(reverseArray([]), []);
  assert.deepStrictEqual(reverseArray([1]), [1]);
});
